import React,{useState} from 'react'

const Movielist = () => {

  const [search,setSearch] = useState('');
  const [data,setData] = useState([]);
  const submitHandler = e =>{
    e.preventDefault();
    fetch(`http://imdb-api.com/en/API/Search/k_12345678/${search}`).then(
      response => response.json()
    ).then(value => {
      console(value.Search);
    })
  }
 
  return (
    <div>
        <center>
          <h1>Search Your Favorite Movie</h1>
          <form onSubmit={submitHandler}>
            <input type="text" value={search} onChange={(e) => setSearch(e.target.value)}/><br /><br />
            <input type="submit" value="Search" />
            </form>
           
            
          <div className="row" >
          {data.map(movie=>
          <div className="col-md-4" key={movie.imdbID}>
            <div className="card" style={{"width": "18rem"}}>
              <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
              <div className="card-body">
                <h4 className="card-title">{movie.Title}</h4>
              
              </div>
            </div>
          </div>
          )}
            </div>
        </center>
    </div>
  )
}

export default Movielist
