import React,{useState} from 'react'
import "./App.css"

const Login = () => {
  const [email,setEmail] =useState()
  const [password,setpassword] =useState()
    const changeHandler = e =>{
        e.preventDefault()
        setEmail("")
        setpassword("")
    }
    function onChangehandle(e){
        setEmail(e.target.value)
    }
    function onChangehandl(e){
        setpassword(e.target.value)
    }
       
           
    
    return (
        <div class="background">
            <center>
                <form autoComplete="off" class="center">
                    <h1>Sign in your account</h1>
                    <input type="email" name="email" value={email} placeholder="Email" onChange={onChangehandle}/><br />
                    <input type="password" name="password" value={password} placeholder="Password" onChange={onChangehandl}/><br />
                    <button class="button" onClick={changeHandler }>Log In</button> &nbsp;&nbsp;
 

                </form>
            </center>
        </div>
    )
}

export default Login
