import React from 'react'
import Movielist from './Movielist'
import "./App.css"
import { useState } from 'react'
import { Outlet, Link } from "react-router-dom";
export default function Login() {
const [email,setEmail] = useState()
const [password,setpassword] = useState()
function changeHandler(e){
  setEmail(e.target.value)
 
}
function changeHandle(e){
  
  setpassword(e.target.value)
}
function clickhandler(e){
  e.preventDefault();
  setEmail("")
  setpassword("")
}
  return (
    <div className="background ">
      <div className='center'>
      <center  >
        <h1>Sign in to your account</h1>
  <input class="search" type="email" name="email" placeholder='email' value={email} onChange={changeHandler}/><br /><br />
  <input class="search" type="password" name="password"  placeholder='password' value={password} onChange={changeHandle}/><br /><br />
  <Link to="/movielist">
  <button className='button' onClick={ clickhandler}
>LOG IN</button> 
</Link>
  </center>
  </div>
{(email ==="" && password ==="" )? <Movielist /> :"please log in"}
 
    </div>
  )
}
